CREATE TABLE `lb_bill_config` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(128) NOT NULL COMMENT '账单名称',
  `config` varchar(1024) DEFAULT NULL COMMENT 'json格式，配置了每个字段，及该字段来源哪个模型：商户，交易，业务账单',
