  KEY `角色限制` (`sysrole_id`),
  CONSTRAINT `角色限制` FOREIGN KEY (`sysrole_id`) REFERENCES `sysrole` (`sysrole_id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统用户表';

